sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
 var image_sys_id=inputs.attachment;
var ga = new GlideSysAttachment();
var gr = new GlideRecord('sys_attachment');
if (gr.get(image_sys_id)) {
    var base64 = ga.getContentBase64(gr);
  	outputs.base64=base64;
    //gs.info(base64);
}
else{
	gs.warn("Cannot access the attachment file. ");
  	outputs.base64="";
}

})(inputs, outputs);

